mapper.select:
SELECT (ROW_NUMBER() OVER (ORDER BY A.create_date DESC)) AS rowNum ,A.alarm_no AS alarmNo , A.alarm_code AS alarmCode , A.alarm_name AS alarmName , A.tag_code AS tagCode , B.tag_name AS tagName , B.tag_realname AS tagRealname , B.sensor_code AS sensorCode , C.sensor_name AS sensorName , C.device_code AS deviceCode , D.device_name AS deviceName , D.location_code AS locationCode , E.location_name AS locationName , A.warning_value AS warningValue , A.alarm_value AS alarmValue , A.alarm_status AS alarmStatus , CASE WHEN A.alarm_status = 0 THEN '정상' WHEN A.alarm_status = 1 THEN '경고' WHEN A.alarm_status = 2 THEN '알람' END AS alarmStatusStr , A.use_yn AS useYn , A.create_date AS createDate , A.create_user AS createUser , A.update_date AS updateDate , A.update_user AS updateUser FROM public.turck_admin_alarm_master A LEFT JOIN turck_admin_tag_master B ON A.tag_code = B.tag_code LEFT JOIN turck_admin_sensor_master C ON B.sensor_code = C.sensor_code LEFT JOIN turck_admin_device_master D ON C.device_code = D.device_code LEFT JOIN turck_admin_location_master E ON D.location_code = E.location_code WHERE 1=1
AND B.tag_name = #{tagName}
AND B.tag_code = #{tagCode}
AND C.sensor_code = #{sensorCode}
AND D.device_code = #{deviceCode}
AND E.location_code = #{locationCode}
AND A.alarm_status = #{alarmStatus}
ORDER BY A.create_date DESC

mapper.insert:
INSERT INTO turck_admin_alarm_master(
      alarm_no
    , tag_code
    , alarm_code
    , alarm_name
    , warning_value
    , alarm_value
    , alarm_status
    , use_yn
    , create_date
    , create_user)
VALUES (
       #{alarmNo}
    , #{tagCode}
    , #{alarmCode}
    , #{alarmName}
    , #{warningValue}
    , #{alarmValue}
    , #{alarmStatus}
    , 1
    , NOW()
    , #{createUser})

mapper.select:
SELECT alarm_code AS alarmCode
FROM turck_admin_alarm_master
WHERE tag_code =  #{tagCode}

mapper.update:
UPDATE turck_admin_alarm_master
SET update_date = NOW()
, update_user = #{updateUser}

, alarm_no = #{alarmNo}


, alarm_name = #{alarmName}


, tag_code = #{tagCode}


, warning_value = #{warningValue}


, alarm_value = #{alarmValue}


, alarm_status = #{alarmStatus}

WHERE alarm_code = #{alarmCode}

mapper.select:
SELECT B.tag_name AS tagName , B.sensor_code AS sensorCode , A.warning_value AS warningValue , A.alarm_value AS alarmValue FROM public.turck_admin_alarm_master A LEFT JOIN turck_admin_tag_master B ON A.tag_code = B.tag_code
WHERE sensor_code = #{sensorCode}
ORDER BY A.create_date DESC

SELECT alarm_status
FROM turck_admin_alarm_master

    WHERE alarm_code = #{alarmCode}

mapper.update:
UPDATE turck_admin_alarm_history_table
SET update_date = NOW()
  , update_user = #{updateUser}
  , clear_status = 0
  , clear_date = NOW()
WHERE alarm_code = #{alarmCode}
  AND alarm_status > #{alarmStatus}

mapper.select:
SELECT MIN(A.create_date) AS startDate , MAX(A.create_date) AS endDate FROM public.turck_admin_alarm_history_table A LEFT JOIN turck_admin_alarm_master B ON A.alarm_code = B.alarm_code LEFT JOIN turck_admin_tag_master C ON B.tag_code = C.tag_code LEFT JOIN turck_admin_sensor_master D ON C.sensor_code = D.sensor_code LEFT JOIN turck_admin_device_master E ON D.device_code = E.device_code LEFT JOIN turck_admin_location_master F ON E.location_code = F.location_code WHERE 1=1
AND C.tag_code = #{tagCode}
AND D.sensor_code = #{sensorCode}
AND E.device_code = #{deviceCode}
AND F.location_code = #{locationCode}
AND A.alarm_status = #{alarmStatus}
AND A.confirm_status = 1

SELECT (ROW_NUMBER() OVER (ORDER BY A.create_date DESC)) AS rowNum , A.alarm_code AS alarmCode , B.alarm_name AS alarmName , C.tag_name AS tagName , D.sensor_name AS sensorName , E.device_name AS deviceName , F.location_name AS locationName , A.alarm_status AS alarmStatus , CASE WHEN A.alarm_status = 0 THEN '정상' WHEN A.alarm_status = 1 THEN '경고' WHEN A.alarm_status = 2 THEN '알람' END AS alarmStatusStr , A.clear_status AS clearStatus , CASE WHEN A.clear_status = 0 THEN '해제' WHEN A.clear_status = 1 THEN '등록' END AS clearStatusStr , A.alarm_value AS alarmValue , A.tag_value AS tagValue , A.clear_date AS clearDate , TO_CHAR(A.clear_date, 'YYYY-MM-DD HH24:MI:SS.MS') AS clearDateStr , A.confirm_status AS confirmStatus , CASE WHEN A.confirm_status = 0 THEN '확인' WHEN A.confirm_status = 1 THEN '미확인' END AS confirmStatusStr , A.confirm_date AS confirmDate , TO_CHAR(A.confirm_date, 'YYYY-MM-DD HH24:MI:SS.MS') AS confirmDateStr , A.confirm_user AS confirmUser , A.use_yn AS useYn , A.create_date AS createDate , TO_CHAR(A.create_date, 'YYYY-MM-DD HH24:MI:SS.MS') AS createDateStr , A.create_user AS createUser , A.update_date AS updateDate , A.update_user AS updateUser , G.user_name AS userName FROM public.turck_admin_alarm_history_table A LEFT JOIN turck_admin_alarm_master B ON A.alarm_code = B.alarm_code LEFT JOIN turck_admin_tag_master C ON B.tag_code = C.tag_code LEFT JOIN turck_admin_sensor_master D ON C.sensor_code = D.sensor_code LEFT JOIN turck_admin_device_master E ON D.device_code = E.device_code LEFT JOIN turck_admin_location_master F ON E.location_code = F.location_code LEFT JOIN core_system_user_master G ON A.update_user = G.user_id WHERE 1=1
AND C.tag_code = #{tagCode}
AND D.sensor_code = #{sensorCode}
AND E.device_code = #{deviceCode}
AND F.location_code = #{locationCode}
AND A.alarm_status = #{alarmStatus}
AND A.create_date >= TO_TIMESTAMP(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
AND TO_TIMESTAMP(#{endDate}, 'YYYY-MM-DD HH24:MI:SS') > A.create_date
AND A.confirm_status = #{confirmStatus}
ORDER BY A.create_date DESC

mapper.update:
UPDATE turck_admin_alarm_history_table
SET update_date = NOW()
  , update_user = #{updateUser}
  , confirm_status = 0
  , confirm_date = NOW()
WHERE sensor_code = #{sensorCode}
